function userAlert(n,t,i){$("#title-toast").text(n);$("#description-toast").text(t);switch(i){case"danger":$("#liveToast").css("background-color","#D34346");break;case"warning":$("#liveToast").css("background-color","#FCE852");break;case"success":$("#liveToast").css("background-color","#43D36A")}var u=document.getElementById("liveToast"),r=bootstrap.Toast.getOrCreateInstance(u);r._config.delay=5e3;r.show()}function userAlertSuccess(){$("#title-toast").text("OK");$("#description-toast").text("Atividade realizada com sucesso.");$("#liveToast").css("background-color","#43D36A");var t=document.getElementById("liveToast"),n=bootstrap.Toast.getOrCreateInstance(t);n._config.delay=5e3;n.show()}